cardsarr.each do |card_datum|
    if card_datum[:tcgplayer_id].nil?
        next
    end
    card = Card.new

    card_cols.each do |key|

        if key == :image_uris
            card[key] = card_datum[key].to_json
        else
            card[key] = card_datum[key]
        end
    end
    card.save!
